//**************** CONTACT *********************/
//**********************************************/
.contact {
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background: linear-gradient(180deg, rgb(15, 15, 15) 50%, rgb(69, 69, 69) 100%);
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    scroll-behavior: smooth;
    width: 100%;
    height: 100vh;
    padding: 0.5em;

    @media (min-width:768px) {
        flex-direction: row;
    }

    article {
        position: relative;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        justify-content: space-between;
        padding: 1em;

        @media (min-width:768px) {
            flex-direction: row;
        }

        >div {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            flex-direction: column;

            @media (min-width:768px) {
                margin-right: 4em;
            }

            @media (min-width:1024px) {
                margin-right: 4em;
            }

            h2 {
                color: #00DCFF;
                font-size: 2em;
                margin-bottom: 2em;
            }

            h4 {
                color: rgb(218, 229, 255);
                margin-top: -2em;
            }

            >a {
                color: rgb(218, 229, 255);
                margin-top: -1em;
                font-size: 1em;
                line-height: 1em;
                text-align: center;
                text-decoration: none;
                margin-top: 1em;
                transition: all 0.4s ease;
            }

            >p {
                color: white;
                margin-top: 0;
            }

            >a:hover {
                transform: scale(1.1);
                transition: all 0.4s ease;
                cursor: pointer;
                color: #00d5ff;
            }
        }

        >div {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;

            label {
                position: relative;
                display: flex;
                flex-direction: column;
                color: #00DCFF;
                text-align: center;
                font-size: 0.6em;
                margin-bottom: 1em;
            }

            >div {
                position: relative;

                iframe {
                    border-radius: 15px;
                    box-shadow: 0px 1px 8px 0.1px #000000;
                }
            }
        }
    }
}
