.our-services {
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  background: linear-gradient(182deg, rgb(9, 9, 9) 50%, #545454 100%);

  h1 {
    color: #ffffffe5;
    font-size: 2em;
    font-weight: bold;
    margin-top: 0em;

    @media (min-width:1024px) {
      font-size: 40px;
    }

    span {
      color: #00d5ff;
    }
  }

  >article {
    width: 85%;
    height: 18em;

    @media (min-width:480px) {
      width: 55%;
    }

    @media (min-width:1024px) {
      width: 85%;
    }

    .main-carousel {
      width: 100%;
      padding: 0 1em;


      .flickity-viewport {
        width: 100%;

        .flickity-slider {
          width: 100%;

          .carousel-cell {
            display: flex;
            justify-content: center;
            align-items: center;
            width: calc(100%);
            height: 260px;
            margin: 0 2em;

            @media (min-width: 480px) {
              height: 260px;
            }

            @media (min-width:1024px) {
              width: 35%;
              height: 260px;
            }

            /*// ( > ) targeting the first child*/
            >article {
              width: 90%;
              height: 90%;
              position: relative;
              display: flex;
              flex-direction: column;
              justify-content: center;
              align-items: center;
              padding: 1em;
              background: rgb(0, 0, 0);
              border-radius: 5px;
              box-shadow: 0 0 0.9px 1px black;

              &:before,
              &:after {
                content: "";
                position: absolute;
                width: 2px;
                height: 2px;
                border-radius: 100%;
                top: 5px;
                right: 5px;
                background: linear-gradient(140deg, rgba(255, 255, 255, 0.136) 0%, rgba(255, 255, 255, 0.096) 25%);
                z-index: -1;
                transition: all 0.4s ease;
                box-shadow: 0 0 0.9px 1px rgba(27, 255, 1, 0.572);
              }

              &:hover {

                &:before,
                &:after {
                  top: -8px;
                  right: -8px;
                  width: 20px;
                  height: 20px;
                  background: rgba(27, 255, 1, 0.572);
                  transition: all 0.7s ease;
                  z-index: 1;
                }
              }

              h1 {
                font-size: 1.1em;
                color: #00d5ffc1;
                text-align: center;
                line-height: 1.2em;
              }

              small {
                color: #ffffffcb;
                font-size: 0.9em;
                font-family: 'Poppins', sans-serif;
                text-align: center;
                line-height: 1.2em;
              }
            }
          }
        }
      }

      .flickity-button {
        display: flex;
        margin-left: -2em;
        margin-right: -2em;
        background: #7db2ba6f;
      }

      .flickity-button:hover {
        background: #8ff0ff6f;
      }

      .flickity-page-dots {
        margin-left: -1em;
      }
    }
  }

  a {
    div {
      position: absolute;
      display: flex;
      justify-content: center;
      align-items: center;
      width: 2em;
      height: 2em;
      border-radius: 100%;
      margin-left: -1em;
      margin-top: 3.9em;
      cursor: pointer;
      background: #00ddff47;
      box-shadow: 0px 1px 8px 0.2px #0000004d;
      color: #00dcff;
      transition: all 0.4s ease;

      i {
        display: flex;
        justify-content: center;
        align-items: center;
        position: relative;
        text-decoration: none;
        color: #19b9dd;
        transition: all 0.3s ease;
        width: 100%;
        padding: 0.5em;
      }
    }

    div:hover {
      transition: all 0.3s ease;
      background: rgba(114, 196, 255, 0.654);
      border: 0.1px solid rgba(58, 91, 238, 0.327);
      box-shadow: 0px 1px 8px 0.1px #00000068;
    }

    i:hover {
      color: white;
    }
  }
}
